mapper.select:
WITH RECURSIVE CommentHierarchy AS ( SELECT c.comment_id, c.content, c.user_id, c.parent_comment_id, c.article_id, c.created_at, c.is_deleted, 1 AS level, CAST(c.comment_id AS CHAR(255)) AS hierarchy_path, u.nickname FROM comments c JOIN users u ON c.user_id = u.id WHERE c.parent_comment_id = #{commentId}
UNION ALL
SELECT c.comment_id, c.content, c.user_id, c.parent_comment_id, c.article_id, c.created_at, c.is_deleted, ch.level + 1 AS level, CONCAT(ch.hierarchy_path, '-', c.comment_id) AS hierarchy_path, u.nickname FROM comments c INNER JOIN CommentHierarchy ch ON c.parent_comment_id = ch.comment_id JOIN users u ON c.user_id = u.id ) SELECT comment_id, content, user_id, parent_comment_id, article_id, created_at, is_deleted, level, hierarchy_path, nickname FROM CommentHierarchy ORDER BY hierarchy_path ASC

mapper.insert:
INSERT INTO comments ( article_id, user_id, content, parent_comment_id ) VALUES ( #{articleId}, #{userId}, #{content},
NULL
#{parentCommentId, jdbcType=INTEGER}
)

mapper.update:
UPDATE comments
SET content = #{content}
WHERE comment_id = #{commentId}

UPDATE comments
SET is_deleted = 1
WHERE comment_id = #{commentId}
